    ProgressDialog mProgressDialog;

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // Carga la información desde internet
        new CargaInfo().execute();
    }

    // Clase para cargar la información relativa a eventos
    private class CargaInfo extends AsyncTask<Void, Void, Void> {
        List<Evento> eventos;

        @Override
        protected void onPreExecute() {
            super.onPreExecute();
            // Cuadro de carga
            mProgressDialog = new ProgressDialog(MainActivity.this);
            mProgressDialog.setTitle("Agenda Link by UMA");
            mProgressDialog.setMessage("Cargando eventos...");
            mProgressDialog.setIndeterminate(false);
            mProgressDialog.show();
        }

        @Override
        protected Void doInBackground(Void... params) {
            eventos = new ArrayList<Evento>();
            // Conectar a la API y obtener JSON de la consulta (esto hay que cambiarlo lógicamente XD)
            JSONObject consulta = null;
            try {
                consulta = new JSONObject(resultadoConsulta);
                // Obtener info de cada evento
                for (int i=0 ; i<consulta.getInt("count") ; ++i) {
                    try {
                        JSONObject post = consulta.getJSONArray("posts").getJSONObject(i);
                        Evento ev = new Evento();
                        ev.titulo = post.getString("title_plain");
                        ev.descripcion = post.getString("content");
//                    InputStream input = new java.net.URL(post.getString("thumbnail")).openStream();
//                    e.imagen = BitmapFactory.decodeStream(input);
                        ev = ObtenerFechasEvento(ev, post.getString("url"));
                        eventos.add(ev);
                    } catch (JSONException e) {
                        e.printStackTrace();
                    }
                }
            } catch (JSONException e) {
                e.printStackTrace();
            }

            return null;
        }

        private Evento ObtenerFechasEvento(Evento event, String url) {
            try {
                // Connect to the web site
                Document document = Jsoup.connect(url).timeout(10000).get();
                // Using Elements to get the class data
                Elements fechas = document.select("abbr[class=tribe-events-abbr updated published dtstart]");
                SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd hh:mm a");
                String fecha = fechas.first().attr("title");
                String[] horas = fechas.get(1).html().split("-");
                String horaInicio = "00:00 pm";
                String horaFin = "01:00 pm";
                if (horas.length == 2) {
                    Log.i("hora0:", horas[0]);
                    horaInicio = horas[0];
                    Log.i("hora1:", horas[1]);
                    horaFin = horas[1];
                }
                Date d = sdf.parse(fecha + " " + horaInicio);
                event.fechaInicio = d;
                d = sdf.parse(fecha + " " + horaFin);
                event.fechaFin = d;
            } catch (IOException e) {
                e.printStackTrace();
            } catch (ParseException e1) {
                e1.printStackTrace();
            }
            return event;
        }

        @Override
        protected void onPostExecute(Void result) {
            String separadorEventos = "\n-------------\n";
            TextView txttitle = (TextView) findViewById(R.id.titletxt);
            txttitle.setText("");
            // Añade todos los eventos
            for (Evento e : eventos) {
                txttitle.append(e.toString() + separadorEventos);
            }
            mProgressDialog.dismiss();
        }
    }

